window.addEventListener("scroll", () => {
  const e = document.getElementById("navbar");
  window.scrollY > 10
    ? e.classList.add("scroll")
    : e.classList.remove("scroll");
});
document.addEventListener("DOMContentLoaded", () => {
  const e = document.querySelectorAll(".nav-link"),
    t = document.querySelector(".navbar-collapse");
  e.forEach((l) => {
    l.addEventListener("click", function () {
      e.forEach((c) => c.classList.remove("active")),
        this.classList.add("active");
      new bootstrap.Collapse(t, { toggle: !1 }).hide();
    });
  }),
    document.addEventListener("click", (l) => {
      t.classList.contains("show") &&
        !t.contains(l.target) &&
        !l.target.classList.contains("navbar-toggler") &&
        new bootstrap.Collapse(t, { toggle: !1 }).hide();
    });
});
function cargar(e) {
  fetch(e)
    .then((t) => t.text())
    .then((t) => {
      document.getElementById("contenido").innerHTML = t;
      const l = document.getElementById("comentarios-clientes");
      e === "page/inicio.html"
        ? (l.style.display = "block")
        : (l.style.display = "none"),
        window.scrollTo({ top: 0, behavior: "smooth" });
    });
}
cargar("page/inicio.html");
const backToTop = document.querySelector(".back-to-top");
window.addEventListener("scroll", () => {
  window.scrollY > 200
    ? backToTop.classList.add("show")
    : backToTop.classList.remove("show");
});
backToTop.addEventListener("click", (e) => {
  e.preventDefault(), window.scrollTo({ top: 0, behavior: "smooth" });
});
